    box-shadow: none;
    color: #ufc1;
    display: block;
    font: 400 15px/2em \\\'Open Sans\\\', sans-serif;
    height: 50px;
    line-height: 2.5em;
    padding: 0 20px;
    transition: all .25s;
    width: 100%;
}
.coursepress .cp-mask .bbm-modal__section form label input:focus {
    background: #ufc0;
    border-color: #ufc9;
}
.coursepress .cp-mask .bbm-modal__section form label input.has-error {
    border-color: #ufc7;
}
.coursepress .cp-mask .bbm-modal__bottombar {
    margin: 15px 0 0;
    padding: 0;
}
.coursepress .units-archive-list li:before,
.coursepress .coursepress-content li.submenu-item:before,
.coursepress .course-structure-block .tree li:before {
    display: none;
}
.coursepress-content .unit-page-feature-image {
    margin: 0 auto 30px;
}
.coursepress-content .unit-page-feature-image img {
    display: block;
